	}
	
	public function processAcquirerResponseCode(Customweb_BNPMercanet_Authorization_Transaction $transaction, array $parameters) {}

	public function getVisibleFormFields(Customweb_Payment_Authorization_IOrderContext $orderContext, $aliasTransaction, $failedTransaction, $paymentCustomerContext){
		$elements = array();
		if ($this->isNFoisActivated()) {
			$settingsHandler = $this->getSettingshandler();
			$validPlans = array();
			for($i=0; $i<Customweb_BNPMercanet_BackendOperation_Form_NFois::PAYMENT_PLAN_COUNT; $i++) {
				$id= 'plan'.$i;
				if($settingsHandler->getSettingValue($id.'_active') != 'yes') {
					continue;
				}
				
				$name = $settingsHandler->getSettingValue($id . '_name');
				$min = $settingsHandler->getSettingValue($id . '_min');
				$max = $settingsHandler->getSettingValue($id . '_max');
				$number = $settingsHandler->getSettingValue($id . '_number');
				$period = $settingsHandler->getSettingValue($id . '_period');
				$first = $settingsHandler->getSettingValue($id . '_first');
				$errors = Customweb_BNPMercanet_Util::getPlanErrors($name, $min, $max, $number, $period, $first);
				if (!empty($errors)) {
					continue;
				}
				if(Customweb_Util_Currency::compareAmount($orderContext->getOrderAmountInDecimals(), $min, $orderContext->getCurrencyCode()) < 0){
					continue;
				}
				if(Customweb_Util_Currency::compareAmount($max, $orderContext->getOrderAmountInDecimals(),  $orderContext->getCurrencyCode()) < 0){
					continue;
				}
				$validPlans[$id] = $name;
			}
			if(!empty($validPlans)) {
				$options = array_merge(array('single' => Customweb_I18n_Translation::__('Single Payment')), $validPlans);
				$planControl = new Customweb_Form_Control_Select('nFoisPlan', $options, 'single');
				$planElement = new Customweb_Form_Element(Customweb_I18n_Translation::__('Payment Plan'), $planControl);
				$elements[] = $planElement;
			}
			
		}
		return $elements;
	}

	public function getRedirectionAuthorizationFields(Customweb_BNPMercanet_Authorization_Transaction $transaction, array $formData){
		$fields = $this->getCaptureModeFields($transaction);
		$parameters = $this->getPaymentMethodParameters();
		$fields['paymentMeanBrandList'] = $parameters['brand'];
		
		if (isset($formData['nFoisPlan']) && !$this->isNFoisActivated()){
			throw new Exception(Customweb_I18n_Translation::__('Payment Plan selected, but not activated for this method.'));
		}
		elseif(isset($formData['nFoisPlan']) && $formData['nFoisPlan'] != 'single') {
			$settingsHandler = $this->getSettingshandler();
			$id = $formData['nFoisPlan'];
			$name = $settingsHandler->getSettingValue($id . '_name');
			$min = $settingsHandler->getSettingValue($id . '_min');
			$max = $settingsHandler->getSettingValue($id . '_max');
			$number = $settingsHandler->getSettingValue($id . '_number');
			$period = $settingsHandler->getSettingValue($id . '_period');
			$first = $settingsHandler->getSettingValue($id . '_first');
			$errors = Customweb_BNPMercanet_Util::getPlanErrors($name, $min, $max, $number, $period, $first);
			if (!empty($errors)) {
				throw new Exception(Customweb_I18n_Translation::__('Payment plan with errors selected. Please contact the merchant.'));
			}
			if(Customweb_Util_Currency::compareAmount($transaction->getAuthorizationAmount(), $min, $transaction->getCurrencyCode()) < 0){
				throw new Exception(Customweb_I18n_Translation::__('Payment plan not available for this amount.'));
			}
			if(Customweb_Util_Currency::compareAmount($max, $transaction->getAuthorizationAmount(),  $transaction->getCurrencyCode()) < 0){
				throw new Exception(Customweb_I18n_Translation::__('Payment plan not available for this amount.'));
			}
			$transactionReference = $this->getHelper()->formatTransactionReference($transaction->getExternalTransactionId());
			$transactionReferenceBase = substr($transactionReference, 0, 33).'i';
			
			$dateList = Customweb_Core_DateTime::_()->format('Ymd');
			$transactionReferenceList = $transactionReference;
			for($i=1; $i < $number; $i++){
				$dateList .= ','.Customweb_Core_DateTime::_()->addHours(24*$period*$i)->format('Ymd');
				$transactionReferenceList .= ','.$transactionReferenceBase.($i+1);
			}
			$totalAmount = $transaction->getAuthorizationAmount();
			$totalAmountFormatted = Customweb_BNPMercanet_Util::formatCurrencyAmount($totalAmount, $transaction->getCurrencyCode());
			
			$firstAmount = $transaction->getAuthorizationAmount() * $first / 100;
			$firstAmountFormatted = Customweb_BNPMercanet_Util::formatCurrencyAmount($firstAmount, $transaction->getCurrencyCode());
			
			$otherAmount =  ($totalAmount-$firstAmount) / ($number -1);
			$otherAmountFormatted = Customweb_BNPMercanet_Util::formatCurrencyAmount($otherAmount, $transaction->getCurrencyCode());
			
			$lastAmountFormatted = $totalAmountFormatted-$firstAmountFormatted-(($number-2) * $otherAmountFormatted);
			$amountList = $firstAmountFormatted.',';
			for($i = 0; $i<$number-2; $i++){
				$amountList .= $otherAmountFormatted.',';
			}
			$amountList.=$lastAmountFormatted;
			$fields['instalmentData.number'] = $number;
			$fields['instalmentData.datesList'] = $dateList;
			$fields['instalmentData.transactionReferencesList'] = $transactionReferenceList;
			$fields['instalmentData.amountsList'] = $amountList;
			$fields['paymentPattern'] = 'INSTALMENT';
			if($this->getContainer()->getBean('Customweb_BNPMercanet_Configuration')->isTestMode()){
				// Test mode transaction reference has a random element
				$fields['transactionReference'] = $transactionReference;
			}
			$transaction->addAuthorizationParameters(array('instalmentPlanId' => $id, 'instalmentPlanName' => $name));
		}				
		return $fields;
	}

	public function getCaptureModeFields(Customweb_BNPMercanet_Authorization_Transaction $transaction){
		$capturing = $transaction->getTransactionContext()->getCapturingMode();
		$captureDelay = $this->getPaymentMethodConfigurationValue('capturing');
		$fields = array();
		if ($capturing === null) {
			if ($captureDelay == 'disabled') {
				$fields['captureMode'] = 'VALIDATION';
				$fields['captureDay'] = 99;
			}
			else {
				$fields['captureMode'] = 'AUTHOR_CAPTURE';
				if ($captureDelay != '0') {
					$fields['captureDay'] = (int) $captureDelay;
				}
			}
		}
		elseif ($capturing == Customweb_Payment_Authorization_ITransactionContext::CAPTURING_MODE_DEFERRED) {
			//Legacy handling for capturing mode
			$fields['captureMode'] = 'VALIDATION';
			$fields['captureDay'] = 99;
		}
		else {
			//Legacy handling for capturing mode
			$fields['captureMode'] = 'AUTHOR_CAPTURE';
		}
		$transaction->setCaptureMode($fields['captureMode']);
		return $fields;
	}

	public function isDeliveryAddressAllowed(){
		return true;
	}

	public function isNFoisActivated(){
		if ($this->existsPaymentMethodConfigurationValue('nfois')) {
			if ($this->getPaymentMethodConfigurationValue('nfois') == 'active') {
				return true;
			}
		}
		return false;
	}
	
	public function checkAcquirerResponseCode(){
		return true;
	}
	
	public function isAliasManagerSupported(){
		return !in_array('AliasManager', $this->getNotSupportedFeatures());
	}
	
